  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from '@material-ui/pickers';

Chartkick.use(Highcharts);

const useStyles = makeStyles((theme) => ({
  gridRight: {
      padding: '25px',
  },
  heading: {
      marginBottom: '0px',
  },
  rootTable: {
    width: '100%',

  },
  containerTable: {
    maxHeight: 465,
    minHeight: 465,
  },
  rootHead: {
    flexGrow: 1,
  },
  welcome: {
    textAlign: 'left',
    fontWeight: '250',
    fontSize: '28px'
  },
  signIn: {
    textAlign: 'right',
    fontWeight: '250',
  },
  button: {
    background: 'linear-gradient(#ac1c13, #bf4342)',
    color: 'white',
    fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif',
    fontWeight: '300',
  },
  rootChart: {
    width: '100%',
    padding: '0px',
  },
  chartTitle: {
      fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif',
      fontWeight: '300',
      fontSize: '24px',
      textAlign: 'center',
      padding: '10px'
  },
  titleRate: {
    fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif',
    fontWeight: '300',
    fontSize: '18px',
    textAlign: 'center',
    padding: '0px'
    },
    listLabelRate: {
        fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif',
        fontWeight: '100',
        fontSize: '23px',
        textAlign: 'left'
    },
    form: {
        display: 'flex',
        flexDirection: 'column',
        margin: 'auto',
        width: 'fit-content',
    },
    formControl: {
        marginTop: theme.spacing(2),
        minWidth: 120,
    },
    formControlLabel: {
        marginTop: theme.spacing(1),
    },
}));

const columns = [
    { 
      id: 'bpm', 
      label: 'Beats per Minute',
      align: 'center' 
      },
    {
      id: 'date',
      label: 'Date/Time Recorded',
      minWidth: 170,
      align: 'center',
      format: (value) => value.toLocaleString('en-US'),
    },
  ];

function createData(unNum, bpm, date) {
    return {unNum, bpm, date };
}

export default function Dashboard() {
  const classes = useStyles();

  const [heartData, setHeartData] = useState([]);
  const [rows, setRows] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [data, setData] = useState(JSON.parse("{}"));
  const [avgRate, setAvgRate] = useState(0);
  const [lastRec, setLastRec] = useState(0);
  const [open, setOpen] = React.useState(false);
  const [fullWidth, setFullWidth] = React.useState(true);
  const [maxWidth, setMaxWidth] = React.useState('sm');

    useEffect(() => {
        HeartRateService.getUsers('001072.b2df559565e4408586c5ff8852b01b65.0530').then(response => {
            let arr = [];
            Object.keys(response.data).forEach(function(key) {
                let currDate = moment(response.data[key].date.substring(0, 19));
                let HeartRate = {
                    bpm: response.data[key].bpm,
                    date: currDate,
                    user_id: response.data[key].userid
                }
                arr.push(HeartRate);
            });
            let arrTable = arr.reverse();
            setLastRec(arrTable[0].bpm);
            setHeartData(arrTable);
            let data = [];
            for(let i = 0; i < arrTable.length; i++){
              data.push(createData(i, arrTable[i].bpm, arrTable[i].date.format('lll')));
            }
            setRows(data);
            let chartdata = "{";
            let currAveRate = 0;
            for(let i = 0; i < 10; i++){
                currAveRate += arrTable[i].bpm;
                chartdata += '"' + arrTable[i].date.format() + '": ' + arrTable[i].bpm;
                if(i !== 9){
                    chartdata += ",";
                }
            }
            chartdata += "}"
            setData(JSON.parse(chartdata));
            setAvgRate(Math.round(currAveRate / 10));
        })
    }, [])

    const updateGraph = (newPage, rowsPerPageNew) => {
        let chartdata = "{";
        let currAveRate = 0;
        let count = 0;
        for(let i = (newPage * rowsPerPageNew); i < ((rowsPerPageNew * newPage) + rowsPerPageNew); i++){
            if(i > heartData.length - 1)
            {
                break; 
            }
            currAveRate += heartData[i].bpm;
            chartdata += '"' + heartData[i].date.format() + '": ' + heartData[i].bpm + ',';
            count++;
        }
        chartdata = chartdata.substring(0, chartdata.length - 1)
        chartdata += "}"
        setData(JSON.parse(chartdata));
        setAvgRate(Math.round(currAveRate / count));
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
        updateGraph(newPage, rowsPerPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
        updateGraph(0, +event.target.value);
    };

    const handleClickOpen = () => {
        setOpen(true);
      };

      const handleClose = () => {
        setOpen(false);
      };

      const handleMaxWidthChange = (event) => {
        setMaxWidth(event.target.value);
      };

      const handleFullWidthChange = (event) => {
        setFullWidth(event.target.checked);
    };

  return (
    <div className={classes.root}>
      <Grid container>
        <Grid>
            <SideBar />
        </Grid>
        <Grid lg={10}>
            <div className={classes.gridRight}>
                <div className={classes.heading}>
                    <div className={classes.rootHead}>
                        <Grid container spacing={3}>
                            <Grid className={classes.welcome} item xs={6}>
                                Welcome, Bryce Schmisseur
                                <div style={{fontSize: '20px'}}>This is your Dashboard</div>
                            </Grid>
                            <Grid className={classes.signIn} item xs={6}>
                            <Button className={classes.button} onClick={handleClickOpen}>Select Dates</Button>
                            </Grid>
                        </Grid>
                    </div>
                </div>
                <Grid container>
                    <Grid className={classes.gridRight} xs={6}>
                        <Paper className={classes.rootTable}>
                        <TableContainer className={classes.containerTable}>
                            <Table stickyHeader aria-label="sticky table">
                            <TableHead>
                                <TableRow>
                                {columns.map((column) => (
                                    <TableCell
                                    key={column.id}
                                    align={column.align}
                                    style={{ minWidth: column.minWidth }}
                                    >
                                    {column.label}
                                    </TableCell>
                                ))}
                                </TableRow>
                            </TableHead>
                            <TableBody id="currentRows">
                                {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                                return (
                                    <TableRow hover role="checkbox" tabIndex={-1} key={row.unNum}>
                                    {columns.map((column) => {
                                        const value = row[column.id];
                                        return (
                                        <TableCell key={column.id} align={column.align}>
                                            {column.format && typeof value === 'number' ? column.format(value) : value}
                                        </TableCell>
                                        );
                                    })}
                                    </TableRow>
                                );
                                })}
                            </TableBody>
                            </Table>
                        </TableContainer>
                        <TablePagination
                            rowsPerPageOptions={[10, 25, 100]}
                            component="div"
                            count={rows.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onChangePage={handleChangePage}
                            onChangeRowsPerPage={handleChangeRowsPerPage}
                        />
                        </Paper>
                    </Grid>
                    <Grid className={classes.gridRight} xs={6}>
                        <div>
                            <Paper className={classes.rootGraph}>
                                <div className={classes.chartTitle}>
                                    BPM VS Time
                                </div>
                            <LineChart 
                                titel = "Heart Rate"
                                ytitle= "Beats per Minute (BPM)"
                                xtitle= "Date Time"
                                data={data} />
                            </Paper>
                        </div>
                        <Grid container>
                            <Grid className={classes.gridRight} xs={6}>
                                <Paper style={{padding: '15px'}}>
                                    <div className={classes.titleRate}>Average Heart Rate</div>
                                    <List>
                                        <ListItem>
                                            <ListItemIcon style={{fontSize: 'large'}}><FavoriteIcon /></ListItemIcon>
                                            <ListItemText><Typography className={classes.listLabelRate}>{avgRate} BPM</Typography></ListItemText>
                                        </ListItem>
                                    </List>
                                </Paper>
                            </Grid>
                            <Grid className={classes.gridRight} xs={6}>
                                <Paper style={{padding: '15px'}}>
                                    <div className={classes.titleRate}>Last Recorded</div>
                                    <List>
                                        <ListItem>
                                            <ListItemIcon style={{fontSize: 'large'}}><FavoriteIcon /></ListItemIcon>
                                            <ListItemText><Typography className={classes.listLabelRate}>{lastRec} BPM</Typography></ListItemText>
                                        </ListItem>
                                    </List>
                                </Paper>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
                <Footer />
            </div>
        </Grid>
      </Grid>
      <React.Fragment>
      <Dialog
        fullWidth={fullWidth}
        maxWidth={maxWidth}
        open={open}
        onClose={handleClose}
        aria-labelledby="max-width-dialog-title"
      >
        <DialogTitle id="max-width-dialog-title">Date Selector</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Please select any two dates that you would like to pull information from:
          </DialogContentText>
          <form className={classes.form} noValidate>
            <FormControl className={classes.formControl}>
              <InputLabel htmlFor="max-width">maxWidth</InputLabel>
              <Select
                autoFocus
                value={maxWidth}
                onChange={handleMaxWidthChange}
                inputProps={{
                  name: 'max-width',
                  id: 'max-width',
                }}
              >
                <MenuItem value={false}>false</MenuItem>
                <MenuItem value="xs">xs</MenuItem>
                <MenuItem value="sm">sm</MenuItem>
                <MenuItem value="md">md</MenuItem>
                <MenuItem value="lg">lg</MenuItem>
                <MenuItem value="xl">xl</MenuItem>
              </Select>
            </FormControl>
            <FormControlLabel
              className={classes.formControlLabel}
              control={<Switch checked={fullWidth} onChange={handleFullWidthChange} />}
              label="Full width"
            />
          </form>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </React.Fragment>
    </div>
  );
}
