import React from 'react';
import MediaQuery from 'react-responsive';

import logo from '../../photos/Group.png';

import HelpIcon from '@material-ui/icons/Help';

import clsx from 'clsx';

import { Link } from 'react-router-dom';
import { AppBar, Toolbar, Button, Grid, Drawer, List, ListItem, ListItemIcon, ListItemText, IconButton, Divider } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { ExpandLess, MenuRounded } from '@material-ui/icons';


const useStyles = makeStyles((theme) => ({
  root: {
      // background: 'linear-gradient(#ac1c13, #bf4342)',
      background: '#ac1c13',
      paddingTop: '5px',
      paddingBottom: '5px'
  },
  signin: {
    width: "210px",
    height: "40px",
    cursor: "pointer",
    paddingLeft: "5px",
  },
  learnmore: {
    width: "210px",
    height: "40px",
    cursor: "pointer",
    color: "black",
    background: "white",
    fontFamily: 'sans-serif',
    textTransform: "none",
    borderRadius: '7px',
    '&:hover': {
      background: 'black',
      color: 'white',
    },   
  },
  learnmorefont: {
    fontSize: '18px',
    fontWeight: '500'
  }
}
));

export default function Component(props) {
  const classes = useStyles();

      // Set state for mobile device navbar menu button to be toggled
    const [state, setState] = React.useState({
        top: false,
      });
    
    // Toggle drawer
    const toggleDrawer = (anchor, open) => (event) => {
        if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
            return;
        }
    
        setState({ ...state, [anchor]: open });
    };

    // Dropdown menu for mobile devices
    const list = (anchor) => (
    <div className={clsx(classes.list, { [classes.fullList]: anchor === 'top' })} role="presentation" 
        onClick={toggleDrawer(anchor, false)} onKeyDown={toggleDrawer(anchor, false)} >
        <List style={{ paddingBottom: "0" }}>
            <Link to="/" style={{ textDecoration: 'none', color: 'grey' }}>
                <ListItem button >
                    <div align="center" style={{padding: "10px"}}>
                        <img src={logo} alt="iHeartRate" width="75%"/> 
                    </div>
                </ListItem>
            </Link>
            <Divider />
            <Link to="/projects" style={{ textDecoration: 'none', color: 'grey' }}>
                <ListItem button >
                    <Button className={classes.learnmore}>
                        <HelpIcon style={{fontSize: "medium", paddingRight: '5px'}}/>
                        <p className={classes.learnmorefont}>Learn More</p>
                    </Button>
                </ListItem>
            </Link>
            <Link to="/experience" style={{ textDecoration: 'none', color: 'grey' }}>
                <ListItem button >
                    <div
                        className={classes.signin}
                        id="appleid-signin"
                        data-mode="center-align"
                        data-color="black"
                        data-border="false"
                        data-border-radius="15"
                        data-size="32"
                        data-type="continue"
                    ></div>
                </ListItem>
            </Link>
            <Divider />
            <ListItem button style={{ justifyContent: "flex-end" }}>
                <div align="right">
                    <ListItemIcon><ExpandLess /></ListItemIcon>
                </div>
            </ListItem>
        </List>
    </div>
    );

  return (
    <div>

    <MediaQuery maxWidth={900}>
      <AppBar className={classes.root}>
        <Toolbar style={{ justifyContent: "space-between" }}>
          {['top'].map((anchor) => (
            <React.Fragment key={anchor}>
              <IconButton onClick={toggleDrawer(anchor, true)}>
                <MenuRounded />
              </IconButton>
              <Drawer anchor={anchor} open={state[anchor]} onClose={toggleDrawer(anchor, false)}>
                {list(anchor)}
              </Drawer>
            </React.Fragment>
          ))}
        </Toolbar>
      </AppBar>
    </MediaQuery>

    <MediaQuery minWidth={901}>
    <AppBar position="static" className={classes.root}>
      <Toolbar>
        <div style={{width: "100%", display: "flex", justifyContent: "space-between"}}>
          <img src={logo} alt="Logo" width={300} height={65} />
          <div style={{display: 'flex', alignItems: 'center', justifyContent: 'center'}}>
            <Button className={classes.learnmore}>
              <HelpIcon style={{fontSize: "medium", paddingRight: '5px'}}/>
              <p className={classes.learnmorefont}>Learn More</p>
            </Button>
            <div
              className={classes.signin}
              id="appleid-signin"
              data-mode="center-align"
              data-color="black"
              data-border="false"
              data-border-radius="15"
              data-size="32"
              data-type="continue"
            ></div>
            </div>
        </div>
      </Toolbar>
    </AppBar>
    </MediaQuery>
   </div>
  );
}
