import React from 'react';
import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import AppleSignin from './../home/AppleSignIn';

export default function Header2(props) {

  const content = {
    'header': 'A New Way of Seeing Data',
    'description': 'iHeartRate allows you to see and interact with the data from you apple watch. \n With a single app you have the ability to view the data on a bigger screen.',
    'primary-action': 'Sign up for free',
    'secondary-action': 'Read more',
    ...props.content
  };

  const useStyles = makeStyles((theme) => ({
    root: {
        background: 'linear-gradient(#ac1c13, #bf4342)',
        paddingTop: '5px',
        paddingBottom: '5px'
    },
    signin: {
      width: "210px",
      height: "40px",
      cursor: "pointer",
      paddingLeft: "5px",
    },
    learnmore: {
      width: "210px",
      height: "40px",
      cursor: "pointer",
      color: "black",
      background: "white",
      fontFamily: 'sans-serif',
      textTransform: "none",
      borderRadius: '7px',
      '&:hover': {
        background: 'black',
        color: 'white',
      },   
    },
    learnmorefont: {
      fontSize: '18px',
      fontWeight: '501'
    }
  }
  ));

  return (
    <section>
      <Container maxWidth="md">
        <Box py={8} textAlign="center">
          <Typography style={{fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif', fontWeight: '100'}} variant="h3" component="h2" gutterBottom={true}>{content['header']}</Typography>
          <Typography style={{fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif', fontWeight: '100'}} variant="h5" color="textSecondary" paragraph={true}>{content['description']}</Typography>
          <Box mt={4}>
            <div id="appleid-signin" className={classes.signin} data-color="black" data-border="false" data-type="sign-in"></div>
            <script type="text/javascript" src="https://appleid.cdn-apple.com/appleauth/static/jsapi/appleid/1/en_US/appleid.auth.js"></script>
          </Box>
        </Box>
      </Container>
    </section>
  );
}
