import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import SideBar from './../components/nav/SideBar';
import Table from '@material-ui/core/Table';
import Footer from './../components/nav/Footer';
import HeartRateService from './../services/HeartRateService';
import moment from 'moment';
import Paper from '@material-ui/core/Paper';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Button from '@material-ui/core/Button';
import Chartkick, { LineChart } from 'react-chartkick';
import Highcharts from 'highcharts';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import FavoriteIcon from '@material-ui/icons/Favorite';
import Typography from '@material-ui/core/Typography';
import { set } from 'mongoose';

Chartkick.use(Highcharts);

const useStyles = makeStyles((theme) => ({
  gridRight: {
      padding: '25px',
  },
  heading: {
      marginBottom: '0px',
  },
  rootTable: {
    width: '100%',

  },
  containerTable: {
    maxHeight: 465,
    minHeight: 465,
  },
  rootHead: {
    flexGrow: 1,
  },
  welcome: {
    textAlign: 'left',
    fontWeight: '250',
    fontSize: '28px'
  },
  signIn: {
    textAlign: 'right',
    fontWeight: '250',
  },
  button: {
    background: 'linear-gradient(#ac1c13, #bf4342)',
    color: 'white',
    fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif',
    fontWeight: '300',
  },
  rootChart: {
    width: '100%',
    padding: '0px',
  },
  chartTitle: {
      fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif',
      fontWeight: '300',
      fontSize: '24px',
      textAlign: 'center',
      padding: '10px'
  },
  titleRate: {
    fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif',
    fontWeight: '300',
    fontSize: '18px',
    textAlign: 'center',
    padding: '0px'
    },
    listLabelRate: {
        fontFamily: '-apple-system-body, BlinkMacSystemFont, sans-serif',
        fontWeight: '100',
        fontSize: '23px',
        textAlign: 'left'
    },
}));

const columns = [
    { 
      id: 'bpm', 
      label: 'Beats per Minute',
      align: 'center' 
      },
    {
      id: 'date',
      label: 'Date/Time Recorded',
      minWidth: 170,
      align: 'center',
      format: (value) => value.toLocaleString('en-US'),
    },
  ];

function createData(bpm, date) {
    return { bpm, date };
}

export default function Test() {
  const classes = useStyles();

  const [heartData, setHeartData] = useState([]);
  const [rows, setRows] = useState([]);
  const [page, setPage] = useState(0);
  const [currRows, setCurrRows] = useState([]);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [data, setData] = useState(JSON.parse("{}"));
  const [avgRate, setAvgRate] = useState(0);
  const [lastRec, setLastRec] = useState(0);

    useEffect(() => {
        HeartRateService.getUsers('001072.b2df559565e4408586c5ff8852b01b65.0530').then(response => {
            let arr = [];
            Object.keys(response.data).forEach(function(key) {
                let currDate = moment(response.data[key].date.substring(0, 19));
                let HeartRate = {
                    bpm: response.data[key].bpm,
                    date: currDate,
                    user_id: response.data[key].userid
                }
                arr.push(HeartRate);
            });
            let arrTable = arr.reverse();
            setLastRec(arrTable[0].bpm);
            setHeartData(arrTable);
            let data = [];
            for(let i = 0; i < arrTable.length; i++){
              data.push(createData(arrTable[i].bpm, arrTable[i].date.format('lll')));
            }
            setRows(data);
            let tableData = [];
            let chartdata = "{";
            let currAveRate = 0;
            for(let i = 0; i < 10; i++){
                tableData.push(createData(arrTable[i].bpm, arrTable[i].date.format('lll')));
                currAveRate += arrTable[i].bpm;
                chartdata += '"' + arrTable[i].date.format() + '": ' + arrTable[i].bpm;
                if(i !== 9){
                    chartdata += ",";
                }
            }
            chartdata += "}"
            setData(JSON.parse(chartdata));
            setAvgRate(Math.round(currAveRate / 10));
            setCurrRows(tableData);
        })
    }, [])

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
        let tableData = [];
        let chartdata = "{";
        let currAveRate = 0;
        for(let i = (newPage * rowsPerPage); i < ((rowsPerPage * newPage) + 10); i++){
            if(i > heartData.length - 1)
            {
                break; 
            }
            tableData.push(createData(heartData[i].bpm, heartData[i].date.format('lll')));
            currAveRate += heartData[i].bpm;
            chartdata += '"' + heartData[i].date.format() + '": ' + heartData[i].bpm + ',';
        }
        setCurrRows(tableData);
        chartdata = chartdata.substring(0, chartdata.length - 1)
        chartdata += "}"
        setData(JSON.parse(chartdata));
        setAvgRate(Math.round(currAveRate / 10));
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

  return (
    <div className={classes.root}>
      <Grid container>
        <Grid>
            <SideBar />
        </Grid>
        <Grid lg={10}>
            <div className={classes.gridRight}>
                <div className={classes.heading}>
                    <div className={classes.rootHead}>
                        <Grid container spacing={3}>
                            <Grid className={classes.welcome} item xs={6}>
                                Welcome, Bryce Schmisseur
                                <div style={{fontSize: '20px'}}>This is your Dashboard</div>
                            </Grid>
                            <Grid className={classes.signIn} item xs={6}>
                            <Button className={classes.button}>Select Dates</Button>
                            </Grid>
                        </Grid>
                    </div>
                </div>
                <Grid container>
                    <Grid className={classes.gridRight} xs={6}>
                        <Paper className={classes.rootTable}>
                        <TableContainer className={classes.containerTable}>
                            <Table stickyHeader aria-label="sticky table">
                            <TableHead>
                                <TableRow>
                                {columns.map((column) => (
                                    <TableCell
                                    key={column.id}
                                    align={column.align}
                                    style={{ minWidth: column.minWidth }}
                                    >
                                    {column.label}
                                    </TableCell>
                                ))}
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {currRows.map((row) => {
                                return (
                                    <TableRow hover role="checkbox" tabIndex={-1} key={row.bpm}>
                                    {columns.map((column) => {
                                        const value = row[column.id];
                                        return (
                                        <TableCell key={column.id} align={column.align}>
                                            {column.format && typeof value === 'number' ? column.format(value) : value}
                                        </TableCell>
                                        );
                                    })}
                                    </TableRow>
                                );
                                })}
                            </TableBody>
                            </Table>
                        </TableContainer>
                        <TablePagination
                            rowsPerPageOptions={[10, 25, 100]}
                            component="div"
                            count={rows.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onChangePage={handleChangePage}
                            onChangeRowsPerPage={handleChangeRowsPerPage}
                        />
                        </Paper>
                    </Grid>
                    <Grid className={classes.gridRight} xs={6}>
                        <div>
                            <Paper className={classes.rootGraph}>
                                <div className={classes.chartTitle}>
                                    BPM VS Time
                                </div>
                            <LineChart 
                                titel = "Heart Rate"
                                ytitle= "Beats per Minute (BPM)"
                                xtitle= "Date Time"
                                data={data} />
                            </Paper>
                        </div>
                        <Grid container>
                            <Grid className={classes.gridRight} xs={6}>
                                <Paper style={{padding: '15px'}}>
                                    <div className={classes.titleRate}>Average Heart Rate</div>
                                    <List>
                                        <ListItem>
                                            <ListItemIcon style={{fontSize: 'large'}}><FavoriteIcon /></ListItemIcon>
                                            <ListItemText><Typography className={classes.listLabelRate}>{avgRate} BPM</Typography></ListItemText>
                                        </ListItem>
                                    </List>
                                </Paper>
                            </Grid>
                            <Grid className={classes.gridRight} xs={6}>
                                <Paper style={{padding: '15px'}}>
                                    <div className={classes.titleRate}>Last Recorded</div>
                                    <List>
                                        <ListItem>
                                            <ListItemIcon style={{fontSize: 'large'}}><FavoriteIcon /></ListItemIcon>
                                            <ListItemText><Typography className={classes.listLabelRate}>{lastRec} BPM</Typography></ListItemText>
                                        </ListItem>
                                    </List>
                                </Paper>
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
                <Footer />
            </div>
        </Grid>
      </Grid>
    </div>
  );
}
