import React, { useState, useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import SideBar from './../components/nav/SideBar';
import Table from './../components/dashboard/Table';
import Heading from './../components/dashboard/Heading';
import Graph from './../components/dashboard/Graph';
import Average from './../components/dashboard/Average';
import LastRecored from './../components/dashboard/Live';
import Footer from './../components/nav/Footer';
import HeartRateService from './../services/HeartRateService';
import moment from 'moment';
import Paper from '@material-ui/core/Paper';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';

const useStyles = makeStyles((theme) => ({
  gridRight: {
      padding: '25px',
  },
  heading: {
      marginBottom: '0px',
  },
  rootTable: {
    width: '100%',

  },
  containerTable: {
    maxHeight: 465,
    minHeight: 465,
  },
}));

const columns = [
    { 
      id: 'bpm', 
      label: 'Beats per Minute',
      align: 'center' 
      },
    {
      id: 'date',
      label: 'Date/Time Recorded',
      minWidth: 170,
      align: 'center',
      format: (value) => value.toLocaleString('en-US'),
    },
  ];

function createData(bpm, date) {
    return { bpm, date };
}

export default function Dashboard() {
  const classes = useStyles();

  const [rateList, setRateList] = useState([]);
  const [rows, setRows] = React.useState([]);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);

    useEffect(() => {
        HeartRateService.getUsers('001072.b2df559565e4408586c5ff8852b01b65.0530').then(response => {
            let arr = [];
            Object.keys(response.data).forEach(function(key) {
                let currDate = moment(response.data[key].date.substring(0, 19))
                let HeartRate = {
                    bpm: response.data[key].bpm,
                    date: currDate,
                    user_id: response.data[key].userid
                }
                arr.push(HeartRate);
            });
            setRateList(arr.reverse()); 
            let arrTable = arr.reverse();
            let data = [];
            for(let i = 0; i < arrTable.length; i++){
              data.push(createData(arr[i].bpm, arrTable[i].date.toLocaleString()));
            }
            setRows(data);
        })
    }, [])

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
        };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

  return (
    <div className={classes.root}>
      <Grid container>
        <Grid>
            <SideBar />
        </Grid>
        <Grid lg={10}>
            <div className={classes.gridRight}>
                <div className={classes.heading}>
                    <Heading/>
                </div>
                <Grid container>
                    <Grid className={classes.gridRight} xs={6}>
                        <Table rateList={rateList}/>
                    </Grid>
                    <Grid className={classes.gridRight} xs={6}>
                        <Graph />
                        <Grid container>
                            <Grid className={classes.gridRight} xs={6}>
                                <Average />
                            </Grid>
                            <Grid className={classes.gridRight} xs={6}>
                                <LastRecored /> 
                            </Grid>
                        </Grid>
                    </Grid>
                </Grid>
                <Footer />
            </div>
        </Grid>
      </Grid>
    </div>
  );
}
