import { createMuiTheme } from '@material-ui/core/styles';

const theme = createMuiTheme({
    typography: {
        fontFamily: [
            '-apple-system', 
            'BlinkMacSystemFont', 
            'sans-serif',
        ].join(','),
    },

    background: 'linear-gradient(#1565C0, #b92b27)',
});

export default theme;
